function formSend(e){e.preventDefault();let r=formValidate(form);const t=document.querySelector("input.error");if(t){t.focus();const e=t.getBoundingClientRect().top+window.scrollY-document.querySelector("header").offsetHeight;window.scrollTo({top:e,behavior:"smooth"})}0===r&&form.reset()}function formValidate(e){let r=0,t=document.querySelectorAll(".req");for(let e=0;e<t.length;e++){const o=t[e];formRemoveError(o),o.classList.contains("email")?emailTest(o)&&(formAddError(o),r++):o.classList.contains("number")?numberTest(o)&&(formAddError(o),r++):o.classList.contains("name")?nameTest(o)&&(formAddError(o),r++):"checkbox"===o.getAttribute("type")&&!1===o.checked?(formAddError(o),r++):""===o.value&&(formAddError(o),r++)}return r}function formAddError(e){e.parentElement.classList.add("error"),e.classList.add("error")}function formRemoveError(e){e.parentElement.classList.remove("error"),e.classList.remove("error")}function emailTest(e){return!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,8})+$/.test(e.value)}function numberTest(e){return!/^(0|(\+994([\- ])?)|(994([\- ])?))?([1-9]{2})([\- ])?(\d{3})([\- ])?(\d{2})([\- ])?(\d{2})$/.test(e.value)}function nameTest(e){return!/^[a-zA-Z\-]+$/.test(e.value)}const form=document.getElementById("form");form.addEventListener("submit",formSend);const input=document.querySelectorAll("input");input.length&&input.forEach(e=>{e.addEventListener("input",()=>{e.classList.contains("error")&&formRemoveError(e)})});